CREATE TABLE `facultate2`.`student` (`idstudent` INT UNSIGNED NOT NULL AUTO_INCREMENT , `nume` VARCHAR(250) NOT NULL , `prenume` VARCHAR(250) NOT NULL , `grupa` ENUM('1','2','3','4') NOT NULL , `email` VARCHAR(550) NOT NULL , `data_add` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `status` ENUM('admis','respins','neevaluat') NOT NULL DEFAULT 'neevaluat' , PRIMARY KEY (`idstudent`), UNIQUE (`email`)) ENGINE = InnoDB;
